import './App.css';
import { useState} from 'react';

function App() {
  const [postgresql, setPostgresql] = useState([]);
  const [inputValue, setInputValue] = useState([]);
  const handleSubmit = (event) => {
    event.preventDefault();
    setPostgresql((current) => { 

      /* NLS_INITCAP 
      ORACLE SYNTAX : SELECT nls_initcap('new york') FROM dual;

POSTGRES SYNTAX : SELECT INITCAP ( 'new york' ) ;
      */
      if((/SELECT */i.test(inputValue) === true) && /NLS_INITCAP */i.test(inputValue) === true)
      {
        var initQuery = inputValue.split('_')[1].split('FROM *');
        return [...current, {
           value: `select ${initQuery}`, 
        },]; 

      } 


      /* DECODE */
      if((/DECODE */i.test(inputValue) === true))
      {
        var postgresqlQuery = inputValue.split('(')[1].split(')')[0];
        
        var coloum,c1,result1,c2,result2,default_result;
        
        console.log("3번째",typeof(postgresqlQuery[0]));
        console.log("coloum",typeof(postgresqlQuery[3]));
        const regex = /[^0-9]/g;
        const result = str.replace(regex, "");
        const number = parseInt(result);

        //3번째가 숫자가 아니라 문자면 
        // todo: 조건2 생략시 
        if(typeof(postgresqlQuery[0])==String)
        {
          coloum=postgresqlQuery.split(',')[0];
          c1=postgresqlQuery.split(',')[1];
          result1=postgresqlQuery.split(',')[2];
          default_result=postgresqlQuery.split(',')[3];
        
          return [...current, {
            value: `case ${coloum} when ${c1} then ${result1} else ${default_result}
            end FROM TRACK;` 
         },];

        }
        else{
          coloum=postgresqlQuery.split(',')[0];
          c1=postgresqlQuery.split(',')[1];
          result1=postgresqlQuery.split(',')[2];
          c2=postgresqlQuery.split(',')[3];
          result2=postgresqlQuery.split(',')[4];
          default_result=postgresqlQuery.split(',')[5];
        
        
          return [...current, {
              value: `case ${coloum} when ${c1} then ${result1} when ${c2} then ${result2} else ${default_result}
              end FROM TRACK;` 
          },];
      }
     }


      /* EMPTY BLOB || EMPTY CLOB */
      if((/EMPTY_BLOB() */i.test(inputValue) === true) || /EMPTY_CLOB() */i.test(inputValue) === true )
      {
        var postgresqlQuery;
        if((/EMPTY_BLOB() */i.test(inputValue) === true))
          postgresqlQuery = inputValue.replace('EMPTY_BLOB()','"');
          
        else if((/EMPTY_CLOB() */i.test(inputValue) === true))
          postgresqlQuery = inputValue.replace('EMPTY_CLOB()','"');
       
       return [...current, {
          value: postgresqlQuery,
       },];
     }

      /* CURRENT_DATE */
      if((/SELECT */i.test(inputValue) === true) && /CURRENT_DATE */i.test(inputValue) === true )
      {
       var postgresqlQuery = inputValue;
       return [...current, {
          value: 'SELECT (CURRENT_DATE + CURRENT_TIME(0))::timestamp;',
       },];
     }


       /* BITAND */
       if((/SELECT */i.test(inputValue) === true) && /BITAND */i.test(inputValue) === true )
       //if (inputValue.includes('SELECT') && (inputValue.includes('BITAND'))) 
       {
        var postgresqlQuery = inputValue.split('(')[1].split(')')[0];
        return [...current, {
           value: `select ${postgresqlQuery.split(',')[0]}&${postgresqlQuery.split(',')[1]};`, 
        },];
      }

      /* SELECT BIN_TO_NUM */
      if((/SELECT */i.test(inputValue) === true) && /BIN_TO_NUM */i.test(inputValue) === true )
      //if (inputValue.includes('SELECT') && (inputValue.includes('BIN_TO_NUM'))) 
      {
        var postgresqlQuery = inputValue.split('(')[1].split(')')[0].replace(/,/g,""); 
        return [...current, {
           value: `select B'${postgresqlQuery}'::int;`, 
        },];
      }

      /* FROMDUAL 변환작업 */
      if (/from dual *;/i.test(inputValue) === true) {
        var queryNotIncludeDual = inputValue.split(/FROM/i);
        var postgresqlQuery = queryNotIncludeDual[0];
        return [...current, {
          value: `${postgresqlQuery}`, 
        },];
      }
      else return [...current, {
        value: inputValue, 
      },];
      
    });

    setInputValue("");
    
  };

  return (
    <div>
      <form id="oracle-query" onSubmit={handleSubmit}>
        <input type="text" value ={inputValue}
          onChange={(event) => {
            setInputValue(event.target.value);
        }} />
        <button type="submit">변환</button>
      </form>
      
      <ol>{postgresql.map((item) => (
        <div>
          <span>{item.value}</span>
        </div>
      ))}</ol>
    </div>
  );
}

export default App;
